import React from "react";
import { Link } from 'react-router-dom';
import { Redirect } from 'react-router-dom';

import './LoginForm.css';
import './images/user-solid.svg'
import './images/unlock-alt-solid.svg'
export default class LoginForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        fullName: "",
      password: ""
    };
  }
  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };
  render() {
    const { contact, getPerson } = this.props;

    const { email, password } = this.state;
    return (
      <div className="loginpage">
        <div className="login">Login</div>
      <form className="LoginContenus" onSubmit={this.handleSubmit}>
        <div className="username">
        {/* <label htmlFor="email">Login</label><br/> */}
       <input  name="password" type="password" 
          class="form-control" placeholder="Password"
          onChange={this.props.handleChange} value={this.props.contact.password} />    
        <i className="fas fa-user" ></i>
        </div>
        <div className="password">
        {/* <label htmlFor="email">Password</label><br/> */}
        <input className="logoPassword"
          name="password"
          type="password"
          placeholder="Enter your password"
          value={password}
          onChange={this.handleChange}
        />
        </div>
<div className="buttonLogin" >
        <button className="btnLogin" type="submit">Login</button>
        </div>
      </form>
      </div>
    );
  }

//   handleChange = event => {
//     this.setState({
//       [event.target.name]: event.target.value

//     });
//   };

  handleSubmit = event => {
    console.log("Submitting");
    console.log(this.state);
  };
}
