import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEnvelope, faMobileAlt } from '@fortawesome/free-solid-svg-icons';

class ReserveCard extends Component {
  render() {
    const { reserve, getReserve } = this.props;
    return (
      <div className="reserve-card">
        <p className="card-title">{reserve.item}</p>
        <div className="card-text">
        <h4>
             Requester:
          </h4>
          <p>{reserve.requester}</p>
          <h4>
             Start Date:
          </h4>
          <p>{reserve.startdate}</p>
          <h4>
          Duration :
          </h4>
          <p>{reserve.duration}</p>
          <h4>
             Description:
          </h4>
          <p>{reserve.Comments}</p>
          
        </div>
        <div className="buttons">
          <Link to="/edit-reserve">
            <input
              type="button"
              value="Edit"
              className="edit-button"
              onClick={() => getReserve(reserve, true)}
            />
          </Link>
          <input type="button" value="Delete" className="edit-button" />
        </div>
      </div>
    );
  }
}
export default ReserveCard;
