import React, { Component } from 'react';
import { Link } from 'react-router-dom';

class ReserveCard extends Component {
  render() {
    const { reserve, getReserve } = this.props;
    return (
      <div className="reserve-card">
        <p className="reserve-card-title"></p>
        <tr>
        <div className="card-text">
            
        {/* <p>
             item :
          </p> */}
          <td>
          <p>{reserve.item}</p></td>
        {/* <p>
             Requester:
          </p> */}          <td>

          <p>{reserve.requester}</p></td>
          {/* <p>
             Start Date:
          </p> */}          <td>

          <p>{reserve.startdate}</p></td>
          {/* <p>
          Duration :
          </p> */}          <td>

          <p>{reserve.duration}</p></td>
          {/* <p>
             Description:
          </p> */}          <td>

          <p>{reserve.Comments}</p></td>
          <Link to="/edit-reserve">
            <input
              type="button"
              value="Edit"
              className="edit-button"
              onClick={() => getReserve(reserve, true)}
            />
          </Link>
          <input type="button" value="Delete" className="edit-button" />
        </div>
        </tr>
        {/* <div className="buttons"> */}
          
        {/* </div> */}
      </div>
    );
  }
}
export default ReserveCard;
