import React, { Component } from 'react';
import './App.css';
import NavMenu from './navBar';
import { Link, Route } from 'react-router-dom';
import ContactCard from './contactCard';
import AddContact from './addContact';
import ReserveCard from './reserveCard';
import AddReserve from './addReserve';
import AddTicket from './addTicket';
import HomeTables from './homeTables';
import axios from 'axios';
// import TableContact from './tableContact';
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fullName: '',
      email: '',
      mobile: '',
      city:'',
       contactlist: [],
      reservelist:[],
      id: '',
      item: '',     
      requester: '',
      startdate: '',
      duration:'',
      Comments:'',

      edit: false
    };
  }

  componentDidMount = () => {
    this.getContacts();
    this.getReserves();
  };
  getReserves = () => {
    axios.get('/reserves').then(res =>
      this.setState({
        reservelist: res.data
      })
    );
  };
  getContacts = () => {
    axios.get('/contacts').then(res =>
      this.setState({
        contactlist: res.data
      })
    );
  };
  addResv = () => {
    this.setState({
      edit: false,
      item: '',     
      requester: '',
      startdate: '',
      duration:'',
      Comments:''
    });
  };
  addTick = () => {
    this.setState({
      edit: false,
      requester: '',     
      assignTo: '',
      priority: '',
      statue:'',
      date:'',
      dateLimit:'',
      title:'',
      discription:'',
      file:'',
    });
  };
  addReserve = () => {
    axios
      .post('/add-reserve', {
        item: this.state.item,
        requester: this.state.requester,
        startdate: this.state.startdate,
        duration: this.state.duration,
        Comments: this.state.Comments,
      })
      .then(this.getReserves);
    this.addResv();
  };
  editReserve = () => {
    axios
      .put('/modify-reserve/' + this.state.id, {
        item: this.state.item,
        requester: this.state.requester,
        startdate: this.state.startdate,
        duration: this.state.duration,
        Comments: this.state.Comments,
      })
      .then(this.getReserves);
    this.addResv();
  };
  editTicket = () => {
    axios
      .put('/modify-ticket/' + this.state.id, {
        requester: this.state.requester,
        assignTo: this.state.assignTo,
        priority: this.state.priority,
        statue: this.state.statue,
        date: this.state.date,
        dateLimit: this.state.dateLimit,
        title: this.state.title,
        discription: this.state.discription,
        file: this.state.file,
      })
      .then(this.getTickets);
    this.addTick();
  };
  addTicket = () => {
    axios
      .post('/add-ticket', {
        requester: this.state.requester,
        assignTo: this.state.assignTo,
        priority: this.state.priority,
        statue: this.state.statue,
        date: this.state.date,
        dateLimit: this.state.dateLimit,
        title: this.state.title,
        discription: this.state.discription,
        file: this.state.file,
      })
      .then(this.getTickets);
    this.addTick();
  };
  addContact = () => {
    axios
      .post('/add-contact', {
        fullName: this.state.fullName,
        email: this.state.email,
        mobile: this.state.mobile,
        city: this.state.city,
      })
      .then(this.getContacts);
    this.add();
  };

  editContact = () => {
    axios
      .put('/modify-contact/' + this.state.id, {
        fullName: this.state.fullName,
        email: this.state.email,
        mobile: this.state.mobile,
        city: this.state.city,
      })
      .then(this.getContacts);
    this.add();
  };
  add = () => {
    this.setState({
      edit: false,
      fullName: '',     
      email: '',
      mobile: '',
      city:''
    });
  };
  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };
  getReserve = (reserve, edit) => {
    this.setState({
      id: reserve._id,
      item: reserve.item,
      requester: reserve.requester,
      startdate: reserve.startdate,
      duration: reserve.duration,
      Comments: reserve.Comments,
      edit
    });
  };

  getPerson = (contact, edit) => {
    this.setState({
      id: contact._id,
      fullName: contact.fullName,
      email: contact.email,
      mobile: contact.mobile,
      city: contact.city,
      edit
    });
  };
  render() {
    const { reserve, getReserve } = this.props;

    return (
      <div className="App">
        <NavMenu />


        <div className="bouttonCreate">
        {/* <HomeTables /> */}
          {/* <Link to="/contact-list">
            <button className="button" onClick={this.add}>
              Contact List
            </button>
          </Link> */}
          {/* <Link to="/reserve-list">
            <button className="button" onClick={this.addResv}>
              Reserve List
            </button>
          </Link> */}
          {/* <Link to="/new-contact">
            <button className="button" onClick={this.add}>
              Add Contact
            </button>
          </Link> */}
          {/* <Link to="/new-reserve">
            <button className="button" onClick={this.addResv}>
              Add Reserve
            </button>
          </Link> */}
        </div>
        


        <Route
          path="/contact-list"
          render={() => (
            <div className="contact-list">
              {this.state.contactlist.map(el => (
                <ContactCard contact={el} getPerson={this.getPerson} />
              ))}
            </div>
          )}
        />
        <Route
          path="/reserve-list"
          render={() => (
            <div className="reserve-list">
              
              <table className="tableTickets">
        <thead>
        <tr>         
          <th className="titleTableTicket" colSpan="5"> Item to do</th>
        </tr>
        <tr className="ticketTable">
          <th>Item</th>
          <th>Requester</th>
          <th>Start Date</th>
          <th>Duration</th>
          <th>Description</th>

        </tr>
      </thead>  
      <tbody className="Tickets">
              {this.state.reservelist.map(el =>  (

                <ReserveCard  reserve={el} getReserve={this.getReserve} />
            ) )}  
            
      </tbody>
  </table>
            </div>
          )}
        />
 
        <Route
          path="/(new-contact|edit-contact)/"
          render={() => (
            <AddContact
              handleChange={this.handleChange}
              action={this.state.edit ? this.editContact : this.addContact}
              edit={this.state.edit}
              contact={this.state}
            />
          )}
        />

        <Route
          path="/(new-ticket|edit-ticket)/"
          render={() => (
            <AddTicket
              handleChange={this.handleChange}
              action={this.state.edit ? this.editTicket : this.addTicket}
              edit={this.state.edit}
              ticket={this.state}
            />
          )}
        />

         <Route
          path="/(new-reserve|edit-reserve)/"
          render={() => (
            <AddReserve
              handleChange={this.handleChange}
              action={this.state.edit ? this.editReserve : this.addReserve}
              edit={this.state.edit}
              reserve={this.state}
            />
          )}
        />
      </div>
    );
  }
}

export default App;






