import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from "axios";

class HomeContent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            bug: [],
            statue : "",
        }
    }
    componentDidMount = () => {
        // this.getContacts();
        // this.getReserves();
        this.getTickets();
      };
      getTickets = () => {
        axios.get("/tickets").then(res =>
          this.setState({
             assigned: res.data.filter(el => el.statue === "Assigned" ),
             planned: res.data.filter(el => el.statue === "planned" ),
             pending: res.data.filter(el => el.statue === "pending" ),
             solved: res.data.filter(el => el.statue === "Bug" ),
             closed: res.data.filter(el => el.statue === "Bug" ),
        
        
        
        
        })
        );
      };
  render() {
   
    return (
      <div className="">
        <table className="tableReservations">
      <thead>
      <tr>         
        <th className="titleTableTicket" colSpan="2" style={{fontSize: '20px'}}> Tickets</th>
      </tr>
      <tr className="ticketTable">
       
        <th>Status</th>
        <th>Number</th>

      </tr>
    </thead> 
              
    <tbody>
        <td>Bug</td>
           <td> {this.state.bug.length}</td>
          
    </tbody>
</table>
        </div> 
    );
  }
}
export default HomeContent;
