import React, { Component } from "react";
import { Link, Route } from "react-router-dom";
import ContactCard from "./contactCard";
import AddContact from "./addContact";
import axios from "axios";

export default class AppContact extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fullName: "",
      email: "",
      mobile: "",
      city: "",
      contactlist: []
    };
  }
  getContacts = () => {
    axios.get("/contacts").then(res =>
      this.setState({
        contactlist: res.data
      })
    );
  };
  addContact = () => {
    axios
      .post("/add-contact", {
        fullName: this.state.fullName,
        email: this.state.email,
        mobile: this.state.mobile,
        city: this.state.city
      })
      .then(this.getContacts);
    this.add();
  };
  editContact = () => {
    axios
      .put("/modify-contact/" + this.state.id, {
        fullName: this.state.fullName,
        email: this.state.email,
        mobile: this.state.mobile,
        city: this.state.city
      })
      .then(this.getContacts);
    this.add();
  };
  add = () => {
    this.setState({
      edit: false,
      fullName: "",
      email: "",
      mobile: "",
      city: ""
    });
  };
  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };
  getPerson = (contact, edit) => {
    this.setState({
      id: contact._id,
      fullName: contact.fullName,
      email: contact.email,
      mobile: contact.mobile,
      city: contact.city,
      edit
    });
  };
  componentDidMount = () => {
    this.getContacts();
  };
  render() {
    return (
      <div>
        <Route
          path="/contact-list"
          render={() => (
            <div className="contact-list">

<table className="tableReservations">
      <thead>
      <tr>         
        <th className="titleTableTicket" colSpan="4" style={{fontSize: '20px'}}> Contacts</th>
      </tr>
      <tr className="ticketTable">
        <th>Name</th>
        <th>email</th>
        <th>Phone</th>
        <th>City</th>
      </tr>
    </thead>  
    <tbody>
            {this.state.contactlist.map(el =>  
              <tr>
              {/* <td>{el.fullName}</td>
              <td>{el.email}</td>
              <td> {el.mobile}</td>
              <td>{el.city}</td> */}
              <td> <ContactCard contact={el} getPerson={this.getPerson}/> </td>
              </tr> 
 )}         
    </tbody>
</table>
              {/* {this.state.contactlist.map(el => (
                <ContactCard contact={el} getPerson={this.getPerson} />
              ))} */}
            </div>
          )}
        />
 <Route
          path="/(new-contact|edit-contact)/"
          render={() => (
            <AddContact
              handleChange={this.handleChange}
              action={this.state.edit ? this.editContact : this.addContact}
              edit={this.state.edit}
              contact={this.state}
            />
          )}
        /> 


      </div>
    );
  }
}
