import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';

class AddContact extends Component {
  constructor(props) {
    super(props);
    this.state = {
      redirect: false
    };
  }

  render() {
    return this.state.redirect ? (
      <Redirect to="/contact-list" />
    ) : (
      <div className="add-card">
        <p className="card-title-add">
          {this.props.edit ? 'Edit Contact' : 'New Contact'}
        </p>
        <input
          name="fullName"
          type="text"
          placeholder="Name..."
          onChange={this.props.handleChange}
          value={this.props.contact.fullName}
        />
          <input
          name="email"
          type="text"
          placeholder="Email..."
          onChange={this.props.handleChange}
          value={this.props.contact.email}
        />
        <input
          name="mobile"
          type="number"
          placeholder="Phone..."
          onChange={this.props.handleChange}
          value={this.props.contact.mobile}
        />
         <input
          name="city"
          type="text"
          placeholder="city..."
          onChange={this.props.handleChange}
          value={this.props.contact.city}
        />
      
        <input
          type="button"
          value={this.props.edit ? 'Edit contact' : 'Add Contact'}
          className="add-button"
          onClick={() => {
            this.props.action();
            this.setState({ redirect: true });
          }}
        />
      </div>
    );
  }
}
export default AddContact;
