import React, { Component } from "react";
import "./App.css";
import NavMenu from "./navBar";
import { Link, Route } from "react-router-dom";
import AddTicket from "./addTicket";
import TicketCard from "./ticketCard";
import AppContact from "./AppContact";
import AppReserve from "./AppReserve";
import axios from "axios";

import HomeTables from "./homeTables";
import ContactCard from "./contactCard";
import AddContact from "./addContact";
import ReserveCard from "./reserveCard";
import AddReserve from "./addReserve";
// import TableContact from './tableContact';
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // fullName: '',
      // email: '',
      // mobile: '',
      // city:'',
          // id: '',
      // item: '',
      // requester: '',
      // startdate: '',
      // duration:'',
      // Comments:'',
      contactlist: [],
      reservelist: [],
      ticketlist: [],
      requesterTicket: "",
      assignTo: "",
      priority: "",
      statue: "",
      date: "",
      dateLimit: "",
      title: "",
      discription: "",
      file: "",
      edit: false
    };
  }
  componentDidMount = () => {
    // this.getContacts();
    // this.getReserves();
    this.getTickets();
  };
  // getReserves = () => {
  //   axios.get('/reserves').then(res =>
  //     this.setState({
  //       reservelist: res.data
  //     })
  //   );
  // };
  // getContacts = () => {
  //   axios.get('/contacts').then(res =>
  //     this.setState({
  //       contactlist: res.data
  //     })
  //   );
  // };
  getTickets = () => {
    axios.get("/tickets").then(res =>
      this.setState({
        ticketlist: res.data
      })
    );
  };
  // addResv = () => {
  //   this.setState({
  //     edit: false,
  //     item: '',
  //     requester: '',
  //     startdate: '',
  //     duration:'',
  //     Comments:''
  //   });
  // };
  addTick = () => {
    this.setState({
      edit: false,
      requesterTicket: "",
      assignTo: "",
      priority: "",
      statue: "",
      date: "",
      dateLimit: "",
      title: "",
      discription: "",
      file: ""
    });
  };
  // addReserve = () => {
  //   axios
  //     .post('/add-reserve', {
  //       item: this.state.item,
  //       requester: this.state.requester,
  //       startdate: this.state.startdate,
  //       duration: this.state.duration,
  //       Comments: this.state.Comments,
  //     })
  //     .then(this.getReserves);
  //   this.addResv();
  // };
  // editReserve = () => {
  //   axios
  //     .put('/modify-reserve/' + this.state.id, {
  //       item: this.state.item,
  //       requester: this.state.requester,
  //       startdate: this.state.startdate,
  //       duration: this.state.duration,
  //       Comments: this.state.Comments,
  //     })
  //     .then(this.getReserves);
  //   this.addResv();
  // };
  addTicket = () => {
    axios
      .post("/add-ticket", {
        requesterTicket: this.state.requesterTicket,
        assignTo: this.state.assignTo,
        priority: this.state.priority,
        statue: this.state.statue,
        date: this.state.date,
        dateLimit: this.state.dateLimit,
        title: this.state.title,
        discription: this.state.discription,
        file: this.state.file
      })
      .then(this.getTickets);
    this.addTick();
  };
  editTicket = () => {
    axios
      .put("/modify-ticket/" + this.state.id, {
        requesterTicket: this.state.requesterTicket,
        assignTo: this.state.assignTo,
        priority: this.state.priority,
        statue: this.state.statue,
        date: this.state.date,
        dateLimit: this.state.dateLimit,
        title: this.state.title,
        discription: this.state.discription,
        file: this.state.file
      })
      .then(this.getTickets);
    this.addTick();
  };

  // addContact = () => {
  //   axios
  //     .post('/add-contact', {
  //       fullName: this.state.fullName,
  //       email: this.state.email,
  //       mobile: this.state.mobile,
  //       city: this.state.city,
  //     })
  //     .then(this.getContacts);
  //   this.add();
  // };

  // editContact = () => {
  //   axios
  //     .put('/modify-contact/' + this.state.id, {
  //       fullName: this.state.fullName,
  //       email: this.state.email,
  //       mobile: this.state.mobile,
  //       city: this.state.city,
  //     })
  //     .then(this.getContacts);
  //   this.add();
  // };
  // add = () => {
  //   this.setState({
  //     edit: false,
  //     fullName: '',
  //     email: '',
  //     mobile: '',
  //     city:''
  //   });
  // };
  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  getTicket = (ticket, edit) => {
    this.setState({
      id: ticket._id,
      requesterTicket: ticket.requesterTicket,
      assignTo: ticket.assignTo,
      priority: ticket.priority,
      statue: ticket.statue,
      date: ticket.date,
      dateLimit: ticket.dateLimit,
      title: ticket.title,
      discription: ticket.discription,
      file: ticket.file,
      edit
    });
  };
  // getReserve = (reserve, edit) => {
  //   this.setState({
  //     id: reserve._id,
  //     item: reserve.item,
  //     requester: reserve.requester,
  //     startdate: reserve.startdate,
  //     duration: reserve.duration,
  //     Comments: reserve.Comments,
  //     edit
  //   });
  // };
  // getPerson = (contact, edit) => {
  //   this.setState({
  //     id: contact._id,
  //     fullName: contact.fullName,
  //     email: contact.email,
  //     mobile: contact.mobile,
  //     city: contact.city,
  //     edit
  //   });
  // };
  render() {
    return (
      <div className="App">
        <NavMenu />
        <AppReserve />
        <AppContact />
        <Route
          path="/ticket-list"
          render={() => (
            <div className="ticket-list">



<table className="tableReservations">
      <thead>
      <tr>         
        <th className="titleTableTicket" colSpan="9" style={{fontSize: '20px'}}> Tickets</th>
      </tr>
      <tr className="ticketTable">
        <th>Requester</th>
        <th>Assign To</th>
        <th>Priority</th>
        <th>Status</th>
        <th>Date</th>
        <th>Date Limit</th>
        <th>Title</th>
        <th>Discription</th>
        <th>file</th>
      </tr>
    </thead>  
    <tbody>
            {this.state.ticketlist.map(el =>  
              <tr>
              <td>{el.requesterTicket}</td>
              <td>{el.assignTo}</td>
              <td> {el.priority}</td>
              <td>{el.statue}</td>
              <td>{el.date}</td>
              <td>{el.dateLimit}</td>
              <td>{el.title}</td>
              <td>{el.discription}</td>
              <td>{el.file}</td>
              </tr> 
 )}         
    </tbody>
</table>



              {/* <tr>
                <th className="titleTableTicket" colSpan="6">
                  {" "}
                  Ticket List
                </th>
              </tr>
              <tr className="ticketTable">
                <th>Requester</th>
                <th>Assign To</th>
                <th>Priority</th>
                <th>Date</th>
                <th>Title</th>
                <th>Discription</th>
              </tr>
              {this.state.ticketlist.map(el => (
                <TicketCard ticket={el} getTicket={this.getTicket} />
              ))} */}
            </div>
          )}
        />
        {/* <TicketCard getTicket={this.getTicket} /> */}
         < TicketCard/>
        <Route
          path="/(new-ticket|edit-ticket)/"
          render={() => (
            <AddTicket
              handleChange={this.handleChange}
              action={this.state.edit ? this.editTicket : this.addTicket}
              edit={this.state.edit}
              ticket={this.state}
            />
          )}
        />
      </div>
    );
  }
}

export default App;

