      <div className="">
      <table className="tableReservations">
      <thead>
      <tr>         
        <th className="titleTableTicket" colSpan="9" style={{fontSize: '20px'}}> Tickets</th>
      </tr>
      <tr className="ticketTable">
        <th>Requester</th>
        <th>Assign To</th>
        <th>Priority</th>
        <th>Status</th>
        <th>Date</th>
        <th>Date Limit</th>
        <th>Title</th>
        <th>Discription</th>
        <th>file</th>
      </tr>
    </thead>  
    <tbody>
            {this.state.today.map(el =>  
              <tr>
              <td>{el.requesterTicket}</td>
              <td>{el.assignTo}</td>
              <td> {el.priority}</td>
              <td>{el.statue}</td>
              <td>{el.date.toString().substring(0,19).replace('T', '/')}</td>
              <td>{el.dateLimit}</td>
              <td>{el.title}</td>
              <td>{el.discription}</td>
              <td>{el.file}</td>
              </tr> 
 )}         
    </tbody>
</table>
        </div> 
    );
  }
}
