import React, { Component } from 'react';

function WarningBanner(props) {
    if (!props.warn) {
      return null;
    }
  
    return (
      <div className="warning">
        <Link to="/contact-list">
            {/* <button className="button" onClick={this.add}>
              Contact List
            </button> */}
          </Link>
          <Link to="/reserve-list">
            {/* <button className="button" onClick={this.add}>
              Contact List
            </button> */}
          </Link>
      </div>
    );
  }
  
export default class HomeTables extends React.Component {
    constructor(props) {
      super(props);
      this.state = {showTables: false}
      this.handleToggleClick = this.handleToggleClick.bind(this);
    }
  
    handleToggleClick() {
      this.setState(prevState => ({
        showTables: !prevState.showTables
      }));
    }
    
    render() {
      return (
        <div>
          <button onClick={this.handleToggleClick}>
            {this.state.showTables ? 'Hide' :'Show' }
          </button>
            <WarningBanner warn={this.state.showTables} />
        </div>
      );
    }
  }
