import React, { Component } from "react";

export default class AddReserve extends Component {
  constructor(props) {
    super(props);
    this.state = {
      redirect: false
    };
  }
  render() {
    return this.state.redirect ? (
      <Redirect to="/reserve-list" />
    ) : (
      <div className="add-card">
        <p className="card-title-add">
          {this.props.edit ? "Edit reserve" : "New reserve"}
        </p>
        <input
          name="item"
          type="text"
          placeholder="item..."
          onChange={this.props.handleChange}
          value={this.props.contact.item}
        />
        <input
          name="requester"
          type="text"
          placeholder="requester..."
          onChange={this.props.handleChange}
          value={this.props.contact.requester}
        />
         <input
          name="startdate"
          type="date"
          placeholder="startdate..."
          onChange={this.props.handleChange}
          value={this.props.contact.startdate}
        />
         <input
          name="duration"
          type="text"
          placeholder="duration..."
          onChange={this.props.handleChange}
          value={this.props.contact.duration}
        />
         <input
          name="Comments"
          type="text"
          placeholder="Description..."
          onChange={this.props.handleChange}
          value={this.props.contact.Comments}
        />

        

      </div>
    );
  }
}
