import React from "react";
import './LoginForm.css';
import './images/user-solid.svg'
import './images/unlock-alt-solid.svg'
export class LoginForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: ""
    };
  }
  render() {
    const { email, password } = this.state;
    return (
      <div className="loginpage">
        <div className="login">Login</div>
      <form className="LoginContenus" onSubmit={this.handleSubmit}>
        <div className="username">
        {/* <label htmlFor="email">Login</label><br/> */}
        <input className="logoLogin"
        
          name="email"
          type="text"
          placeholder="Enter your login"
          value={email}
          onChange={this.handleChange}
        /><i className="fas fa-user" ></i>
        </div>
        <div className="password">
        {/* <label htmlFor="email">Password</label><br/> */}
        <input className="logoPassword"
          name="password"
          type="password"
          placeholder="Enter your password"
          value={password}
          onChange={this.handleChange}
        />
        </div>

        <button className="buttonLogin" type="submit">Login</button>
      </form>
      </div>
    );
  }

  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value

    });
  };

  handleSubmit = event => {
    console.log("Submitting");
    console.log(this.state);
  };
}
